/* ==UserStyle==
@name        gravatar-chevron
@description better chevron for buttons with icons, i.e. «Read our Privacy Policy»
@version     2023.12.18
@namespace   dym.sh
@author      Dym Sohin
@homepageURL https://dym.sh/gravatar-chevron/
@updateURL   https://source.garden/styles/gravatar-chevron/raw/branch/latest/style.user.css
@supportURL  https://source.garden/styles/gravatar-chevron/issues/
@license     0BSD
==/UserStyle== */

@-moz-document domain("gravatar.com") {

.g-has-chevron-external::after {
  content: "⭟";
  font-weight: 700;
  margin-left: 0.75rem;
  line-height: 0.75rem;
  background: var(--g-color-accent);
  padding: 0.125rem;
  color: var(--g-background);
  border-radius: 0.125rem;
}

.g-has-chevron-external:active::after
, .g-has-chevron-external:hover::after {
  background: var(--g-color);
}


.g-has-chevron-external::after
, .g-has-chevron-external:active::after
, .g-has-chevron-external:hover::after {
  left: unset;
  margin-right: unset;
  top: unset;
  -webkit-transform: unset;
  transform: unset;
  -webkit-transform-origin: unset;
  transform-origin: unset;
}

}

/* ==UserStyle==
@name        translit-dark
@description Dark Mode for Translit.net
@version     2023.10.06
@namespace   translit.net
@author      Dym Sohin
@homepageURL https://dym.sh/translit-dark/
@updateURL   https://source.garden/styles/translit-dark/raw/branch/latest/style.user.css
@supportURL  https://source.garden/styles/translit-dark/issues
@license     0BSD
==/UserStyle== */

@-moz-document domain("translit.net")
{

* { background : transparent !important
  ; color : #bbb !important
  }

body
, .bcharset
  { background : #222 !important }

ul#tTabCell li:nth-of-type(2n)
  { background : #333 !important }

input
, button
, select
, textarea
  { background : #333 !important
  ; border : 1px solid #222 !important
  }

.tTextArea120
, .tWzgBlock120
  { padding-right : 0 !important }

p
, .tBottomContainer
, .tLogoContainer
, .tCookiesButtonA
, .tAdvContainer
, .tBoxAdv
, .tBannerRight120
, #placeholder
  { display : none !important }

}

